        $Allowed_Ext = array('docx','doc','xls','xlsx','csv','ppt','pptx');
        if(in_array($file_ext,$Allowed_Ext)) {
            $file->move($destination, $name_new);
            $file_path = $destination.'/'.$name_new;
            $Conversionapi = new Conversionapi();
