                                    $fieldsResponseArray[$findValue->rectooltip][$fieldsName] = $fieldsValue;
                                } else {
                                    $fieldsResponseArray[$findValue->rectooltip] = [$fieldsName => $fieldsValue];
                                }
                            }
                            foreach($html->find('.dynamicTag') as $findTagKey => $findTagValue) {
                                $tagsResponseArray[$findTagValue->tagname] = $findTagValue->innertext;
