                $templates = Offersent::where('sender_id', $user_id)->where('offer_status', 7)->orderBy('created_date', 'desc')->select('id AS template_id', 'name', 'created_date')->skip(0)->take(3)->get()->toArray();
                foreach($templates as $key => $value) {
